    func testInt() throws {
        var x1 = [Int](repeating: 0, count: 1000)
        var x2 = [Int](repeating: 0, count: 1000)
        let m64 = MT(kind: .MT64)
        var state = m64.getState()
        for i in 0 ..< x1.count {
            x1[i] = m64.randomInt(in: Int.min ... Int.max)
        }
        m64.setState(state: state)
        for i in 0 ..< x2.count {
            x2[i] = m64.randomInt(in: Int.min ... Int.max)
        }
        XCTAssertEqual(x1, x2)
        state = m64.getState()
        for i in 0 ..< x1.count {
            x1[i] = m64.randomInt(in: 0 ... Int.max)
        }
        m64.setState(state: state)
        for i in 0 ..< x2.count {
            x2[i] = m64.randomInt(in: 0 ... Int.max)
        }
        XCTAssertEqual(x1, x2)
        state = m64.getState()
        for i in 0 ..< x1.count {
            x1[i] = m64.randomInt(in: Int.min ... 0)
        }
        m64.setState(state: state)
        for i in 0 ..< x2.count {
            x2[i] = m64.randomInt(in: Int.min ... 0)
        }
        XCTAssertEqual(x1, x2)
    }

