    func testMinMaxInt() throws {
        let m64 = MT(kind: .MT64)
        XCTAssertTrue(m64.randomInt(in: 0 ... 0) == 0)
        XCTAssertTrue(m64.randomInt(in: 0 ..< 1) == 0)
        XCTAssertTrue(m64.randomInt(in: Int.max ... Int.max) == Int.max)
        XCTAssertTrue(m64.randomInt(in: Int.max - 1 ..< Int.max) == Int.max - 1)
        XCTAssertTrue(m64.randomInt(in: Int.min ... Int.min) == Int.min)
        XCTAssertTrue(m64.randomInt(in: Int.min ..< Int.min + 1) == Int.min)
        XCTAssertTrue(m64.randomInt(in: Int32(0) ... Int32(0)) == 0)
        XCTAssertTrue(m64.randomInt(in: Int32(0) ..< Int32(1)) == 0)
        XCTAssertTrue(m64.randomInt(in: Int32.max ... Int32.max) == Int32.max)
        XCTAssertTrue(m64.randomInt(in: Int32.max - 1 ..< Int32.max) == Int32.max - 1)
        XCTAssertTrue(m64.randomInt(in: Int32.min ... Int32.min) == Int32.min)
        XCTAssertTrue(m64.randomInt(in: Int32.min ..< Int32.min + 1) == Int32.min)
        XCTAssertTrue(m64.randomInt(in: Int64(0) ... Int64(0)) == 0)
        XCTAssertTrue(m64.randomInt(in: Int64(0) ..< Int64(1)) == 0)
        XCTAssertTrue(m64.randomInt(in: Int64.max ... Int64.max) == Int64.max)
        XCTAssertTrue(m64.randomInt(in: Int64.max - 1 ..< Int64.max) == Int64.max - 1)
        XCTAssertTrue(m64.randomInt(in: Int64.min ... Int64.min) == Int64.min)
        XCTAssertTrue(m64.randomInt(in: Int64.min ..< Int64.min + 1) == Int64.min)
    }

