    func testFloat() throws {
        let m64 = MT(kind: .MT64)
        for _ in 0 ..< 1000 {
            let x = m64.randomFloat(in: 0.0 ..< 1.0)
            XCTAssertTrue(0.0 <= x && x < 1.0)
        }
        for _ in 0 ..< 1000 {
            let x = m64.randomFloat(in: 0.0 ... 1.0)
            XCTAssertTrue(0.0 <= x && x <= 1.0)
        }
    }

